/*
 * https://leetcode-cn.com/problems/number-of-substrings-containing-all-three-characters/
 */
#include <iostream>
#include <string>
#include <utility>
#include <unordered_set>
#include <unordered_map>
#include <vector>
#include "../util.h"

using namespace std;

class Solution {
public:
    int numberOfSubstrings(string s) {
        int32_t ans = 0;
        vector<pair<int32_t, int32_t> > matched_index_v;
        int32_t left = 0, right = 0;
        unordered_map<char, int32_t> finded_char;


#if 0
        for(; left < s.size(); ++left){
            //finded_char.clear();
            if(left != 0){
                --finded_char[s.at(left - 1)];
            }
            for(; right < s.size(); ){
                if(finded_char.size() == 3 &&
                        finded_char['a'] > 0 && 
                        finded_char['b'] > 0 && 
                        finded_char['c'] > 0){
                    matched_index_v.emplace_back(left, right);
                    break;
                }
                ++right;
                if(right < s.size())
                    ++finded_char[s.at(right)];
            }
        }
#endif
        for(left = 0, right = 0; right < s.size(); ++right){
            ++finded_char[s.at(right)];
            while(finded_char.size() == 3 &&
                    finded_char['a'] > 0 && 
                    finded_char['b'] > 0 && 
                    finded_char['c'] > 0){
                matched_index_v.emplace_back(left, right);
                --finded_char[s.at(left)];
                ++left;
            }
        }
        for(const auto& o:matched_index_v){
            //cout << o.first << ", " << o.second << endl;
            //cout << s.substr(o.first, o.second - o.first + 1) << endl;
            ans += (s.size() - o.second);
        }
        return ans;
    }
};

namespace optimize{
class Solution {
public:
    int numberOfSubstrings(string s) {
        int32_t ans = 0;
        int32_t left = 0, right = 0;
        unordered_map<char, int32_t> finded_char;

        for(left = 0, right = 0; right < s.size(); ++right){
            ++finded_char[s.at(right)];
            while(finded_char.size() == 3 &&
                    finded_char['a'] > 0 && 
                    finded_char['b'] > 0 && 
                    finded_char['c'] > 0){
                ans += (s.size() - right);
                --finded_char[s.at(left)];
                ++left;
            }
        }
        return ans;
    }
};
}

int main(void){
    optimize::Solution sol;
    string s = "";
    //string s = "aaabc";
    int32_t ans = 3;

    //cout <<  "初始: " << s << endl;
    uint64_t t1 = GetNowUsecs();
    ans = sol.numberOfSubstrings(s);
    uint64_t t2 = GetNowUsecs();
    cout << ans << endl;
    cout << "Spend time: " << (t2 - t1) << endl;
    return 0;
}












